import './App.css';
import React, { useState, useEffect } from 'react';
import MyStopwatch from './MyStopwatch'


function App() {
  const [meme,setMeme] = useState([])
  const [currentMeme,setCurrentMeme] = useState(2)
  const [seconds,setSeconds] = useState(30)
  const [isLoaded, setIsLoaded] = useState(false)
  const [currentImage,setCurrentImage] = useState()
  const [nextImage, setNextImage] = useState()

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    )
    .then(data => {
      for (var i =0; i<Object.keys(data.title).length/10; i++){
      setMeme(preState => [
        ...preState, {media:data.media['?',i],
      score:data.score['?',i],
    title:data.title['?',i]}
      ])
    }
    console.log(data)
    console.log(Object.keys(data.title).length)
    const img = new Image();
    img.src = meme[0].media
    setCurrentImage(img)
    })

  },[])

  useEffect(() => {
    const img = new Image();
    img.src = meme[currentImage+1].media
    setNextImage(img)

  },[currentImage])

  useEffect(() => {
    setTimeout(() => {
      if(seconds!=0){
      setSeconds(seconds-1)}
      else{
        setCurrentImage(nextImage)
        setSeconds(30)

      }
    }, 1000);

  }, [seconds])

  useEffect(()=>{
    console.log(meme)
  })

  return (
    <div className="App">
      <header className="App-header">
      {meme[currentMeme]?        <div>
            <h3>{meme[currentMeme].title}</h3>
            <img src={meme[currentMeme].media} onError={(e) => {
              setCurrentMeme(currentMeme+1)
            }}></img>

            {currentImage}

          </div>:null}
        <p>{seconds}</p>
      </header>
    </div>
  );
}

export default App;
