import './App.css';
import React, { useState, useEffect } from 'react';
import MyStopwatch from './MyStopwatch'


function App() {
  const [meme,setMeme] = useState([])
  const [currentMeme,setCurrentMeme] = useState(2)
  const [nextMeme,setNextMeme] = useState(currentMeme+1)
  const [seconds,setSeconds] = useState(15)
  const [displayCurrent, setDisplayCurrent] = useState(true)
  const [length,setLength] = useState()

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    )
    .then(data => {
      setLength(Object.keys(data.title).length)
      for (var i =0; i<Object.keys(data.title).length; i++){
      setMeme(preState => [
        ...preState, {media:data.media['?',i],
      score:data.score['?',i],
    title:data.title['?',i]}
      ])
    }

    let shuffleMeme = [...meme]
    setMeme(shuffle(shuffleMeme))

    console.log(data)
    console.log(Object.keys(data.title).length)
    })

  },[])

  useEffect(() => {
    setTimeout(() => {
      if(seconds!=0){
      setSeconds(seconds-1)}
      else{
        if(displayCurrent){
          setDisplayCurrent(false)
          if(length>(nextMeme+1)){
          setCurrentMeme(nextMeme+1)
          }
          else{
            setCurrentMeme(0)
          }

        }
        else{
          setDisplayCurrent(true)
          if(length>(currentMeme+1)){
          setNextMeme(currentMeme+1)
          }
          else{
            setNextMeme(0)
          }
        }
        setSeconds(15)

      }
    }, 1000);

  }, [seconds])

  useEffect(()=>{
    console.log(meme)
  })

  function shuffle(array) {
    let currentIndex = array.length
    let randomIndex

    while (currentIndex != 0) {

      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex--;

      [array[currentIndex], array[randomIndex]] = [
        array[randomIndex], array[currentIndex]];
    }

    return array

    }

  return (
    <div className="App">
      <header className="App-header">
      {meme[currentMeme+1]?        <div>

        <div style={displayCurrent?null:{display:'none'}}>
            <h3>{meme[currentMeme].title}</h3>
            <img src={meme[currentMeme].media} onError={(e) => {
              setCurrentMeme(currentMeme+1)
            }}></img>
            </div>

            <div style={displayCurrent?{display:'none'}:null}>
            <h3>{meme[nextMeme].title}</h3>
            <img src={meme[nextMeme].media} onError={(e) => {
              setNextMeme(nextMeme+1)
            }}></img>
            </div>
          </div>:null}
        <span className="timer">{seconds}</span>
      </header>
    </div>
  );
}

export default App;
