import './App.css';
import React, { useState, useEffect } from 'react';
import MyStopwatch from './MyStopwatch'


function App() {
  const [meme,setMeme] = useState([])
  const [currentMeme,setCurrentMeme] = useState(2)
  const [nextMeme,setNextMeme] = useState(currentMeme+1)
  const [seconds,setSeconds] = useState(30)
  const [displayCurrent, setDisplayCurrent] = useState(true)

  useEffect(() => {
    fetch("/members").then(
      res => res.json()
    )
    .then(data => {
      for (var i =0; i<Object.keys(data.title).length/10; i++){
      setMeme(preState => [
        ...preState, {media:data.media['?',i],
      score:data.score['?',i],
    title:data.title['?',i]}
      ])
    }
    console.log(data)
    console.log(Object.keys(data.title).length)
    })

  },[])

  useEffect(() => {
    setTimeout(() => {
      if(seconds!=0){
      setSeconds(seconds-1)}
      else{
        if(displayCurrent){
          setDisplayCurrent(false)
          setCurrentMeme(nextMeme+1)

        }
        else{
          setDisplayCurrent(true)
          setNextMeme(currentMeme+1)
        }
        setSeconds(30)

      }
    }, 1000);

  }, [seconds])

  useEffect(()=>{
    console.log(meme)
  })

  return (
    <div className="App">
      <header className="App-header">
      {meme[currentMeme+1]?        <div>
            <h3>{meme[currentMeme].title}</h3>
            <img style={displayCurrent?null:{display:none}} src={meme[currentMeme].media} onError={(e) => {
              setCurrentMeme(currentMeme+1)
            }}></img>
            <img style={displayCurrent?{display:none}:null} src={meme[nextMeme].media} onError={(e) => {
              setNextMeme(nextMeme+1)
            }}></img>
          </div>:null}
        <p>{seconds}</p>
      </header>
    </div>
  );
}

export default App;
